import java.util.Scanner;

import com.bd.zuche.*;

class TestZuChe2 

{

    public static void main(String[] args) 

    {

        Car car = new Car("战神","长城");        //初始化轿车对象car

        car.show();                   //输出车辆信息

        car.drive();                  //让car行驶1次，油量剩下15升，车损度为10

